/** @format */

import React, { useState } from "react";
import Board from "./Board";
import Winner from "./Winner";
import "./App.css";

function App() {
  const [nrows, setNRows] = useState(5);
  const [ncols, setNCols] = useState(5);
  const [restart, setRestart] = useState(false);

  const handleIncreaseRows = () => {
    setNRows((prevNRows) => prevNRows + 1);
  };

  const handleDecreaseRows = () => {
    setNRows((prevNRows) => Math.max(prevNRows - 1, 2));
  };

  const handleIncreaseCols = () => {
    setNCols((prevNCols) => prevNCols + 1);
  };

  const handleDecreaseCols = () => {
    setNCols((prevNCols) => Math.max(prevNCols - 1, 2));
  };
  const handleRestart = () => {
    setRestart((prevRestart) => !prevRestart);
  };

  return (
    <div className="App">
      <h1>Cell Out!</h1>
      <div className="Settings">
        <p>Number of Rows: {nrows}</p>
        <button onClick={handleIncreaseRows}>Increase Rows</button>
        <button onClick={handleDecreaseRows}>Decrease Rows</button>
        <p>Number of Columns: {ncols}</p>
        <button onClick={handleIncreaseCols}>Increase Columns</button>
        <button onClick={handleDecreaseCols}>Decrease Columns</button>
      </div>
      {restart ? (
        <Winner onRestart={handleRestart} />
      ) : (
        <Board nrows={nrows} ncols={ncols} />
      )}
    </div>
  );
}

export default App;
